# sequence = [alphabet.find(i) for i in text]

for length in range(1, 10):
    substr = text[::length]
    # print(substr[:30])
    index = 0
    for letter in alphabet:
        index += substr.count(letter) * (substr.count(letter) - 1)
    index = index / len(substr) / (len(substr) - 1)
    print(length, " | ", "{:.4f}".format(index), " | ", len(substr))

